import React from 'react';
import PropTypes from 'prop-types';
import './Project.css'; // Import the new CSS file

const Project = () => {
  const projects = [
    {
      title: "AgroMaart - Online Fertilizer Shop",
      icon: "ri-shopping-cart-fill",
      description: "Built a user-friendly web application to make fertilizer shop management easier and improve communication between shop owners, farmers, and delivery staff. The app includes Role-Based Access, giving each user group specific tools: shop owners can manage inventory and orders, farmers can browse products, see prices, place orders, and track deliveries, while delivery staff can update delivery status in real-time to keep everyone informed. Spring Security ensures safe login and data protection. This platform has made fertilizer distribution more efficient by connecting all users on one platform, reducing manual work, and boosting transparency in operations.",
      additionalInfo: "Technologies:  Spring Boot, Hibernate, MySQL React, HTML, CSS",
      imageSrc: require('../../assets/images/AgroMart.webp'),
      animation: "fade-left",
    },
    {
      title: "Students Management System",
      icon: "ri-book-2-fill",
      description: "The Students Management System is a web-based application designed to manage student records efficiently. It allows administrators to enroll students, update their information, and retrieve records easily. The system ensures secure access and data accuracy while providing a user-friendly interface for smooth navigation. With features like search and modification, it simplifies student data management. This application helps reduce manual work and enhances overall efficiency in handling academic records",
      additionalInfo: "Technologies: Spring Boot, Hibernate, Thymeleaf, MySQL",
      imageSrc: require('../../assets/images/studentManagementSystem.webp'),
      animation: "fade-left",
    },
  ];

  return (
    <section id="project" className="project-section">
      <div className="project-container">
        <h2 className="project-title">Projects</h2>
        <div className="project-grid">
          {projects.map((project, index) => (
            <TimelineCard key={index} index={index} {...project} />
          ))}
        </div>
      </div>
    </section>
  );
};

const TimelineCard = ({ title, icon, description, additionalInfo, imageSrc, animation }) => {
  return (
    <div className="timeline-card" data-aos={animation}>
      <div className="icon-container">
        <img src={imageSrc} alt={`Icon for ${title}`} className="icon-image" />
      </div>
      <div className="card-content">
        <h3 className="card-title">
          {icon && <i className={`${icon} mr-2`} aria-hidden="true"></i>} {title}
        </h3>
        <p className="card-description">{description}</p>
        {additionalInfo && <p className="card-additional-info">{additionalInfo}</p>}
      </div>
    </div>
  );
};

TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  additionalInfo: PropTypes.string,
  imageSrc: PropTypes.string.isRequired,
  animation: PropTypes.string.isRequired,
};

export default Project;
