import React from 'react';

// Define the styles
const containerStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
  gap: '1.5rem',
  padding: '2rem',
};

const skillsSectionStyle = {
  background: 'linear-gradient(145deg, #ffffff, #f5f5f5)',
  borderRadius: '12px',
  boxShadow: '0px 10px 20px rgba(0, 0, 0, 0.1)',
  padding: '1.5rem',
  textAlign: 'center',
};

const sectionTitleStyle = {
  fontSize: '1.8rem',
  fontWeight: '600',
  marginBottom: '1rem',
  color: '#333',
};

const badgeStyle = {
  margin: '5px',
  display: 'inline-block',
  transition: 'transform 0.3s, box-shadow 0.3s',
};

const badgeHoverStyle = {
  ...badgeStyle,
  transform: 'scale(1.1)',
  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
};

const Skills = () => {
  return (
    <section id="Skills" style={{ padding: '4rem', backgroundColor: '#e8f5e9' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <h2 style={{ textAlign: 'center', fontSize: '2.5rem', fontWeight: '700', marginBottom: '2rem', color: '#007396' }}>
        Skills & Expertise
        </h2>

        <div style={containerStyle}>
          {/* Frontend Section */}
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#1c1c1c', // Darker background for better contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 20px rgba(0, 0, 0, 0.7)', // Deeper shadow for more depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#ffcc00', // Bright yellow for the title
    textAlign: 'center', 
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)', // Text shadow for effect
  }}>
    Frontend
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['JavaScript', 'HTML5', 'CSS3', 'React'].map(skill => {
      const badgeColor = {
        JavaScript: '#f7df1e', // Yellow
        HTML5: '#e34f26', // Red
        CSS3: '#1572b6', // Blue
        React: '#61dafb', // Light Blue
      }[skill];

      return (
        <img
          key={skill}
          src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase()}&logoColor=black`}
          alt={skill}
          style={{
            ...badgeStyle,
            filter: 'brightness(1.2)', // Slightly brighter badges
            transition: 'box-shadow 0.3s, transform 0.3s', 
            margin: '10px', 
            borderRadius: '10px', // More rounded corners
            cursor: 'pointer', 
            boxShadow: `0 0 10px ${badgeColor}`, // Shadow matching badge color
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.boxShadow = '0 0 20px rgba(255, 255, 255, 0.7)'; // Bright glow on hover
            e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.boxShadow = `0 0 10px ${badgeColor}`; // Restore shadow color
            e.currentTarget.style.transform = 'scale(1)'; // Reset scale
          }}
        />
      );
    })}
  </div>
</div>




          {/* Backend Section */}
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#282c34', // Darker background for better contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 20px rgba(0, 0, 0, 0.7)', // Deeper shadow for more depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#61dafb', // Light blue for the title
    textAlign: 'center', 
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)', // Text shadow for effect
  }}>
    Backend
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['Java', 'Node.js', 'CSharp', 'C++'].map(skill => {
      const badgeColor = {
        Java: '#007396', // Java Color
        'Node.js': '#8CC84B', // Node.js Color
        CSharp: '#68217A', // C# Color
        'C++': '#00599C', // C++ Color
      }[skill];

      return (
        <img
          key={skill}
          src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase().replace('.', '-')}&logoColor=white`}
          alt={skill}
          style={{
            margin: '10px',
            borderRadius: '10px',
            filter: 'brightness(1.2)', // Slightly brighter badges
            transition: 'box-shadow 0.3s, transform 0.3s', 
            cursor: 'pointer',
            boxShadow: `0 0 10px ${badgeColor}`, // Shadow matching badge color
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.boxShadow = '0 0 20px rgba(255, 255, 255, 0.7)'; // Bright glow on hover
            e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.boxShadow = `0 0 10px ${badgeColor}`; // Restore shadow color
            e.currentTarget.style.transform = 'scale(1)'; // Reset scale
          }}
        />
      );
    })}
  </div>
</div>

          {/* MERN Stack Section */}
          <div style={skillsSectionStyle}>
  <h3 style={sectionTitleStyle}>MERN Stack</h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['MongoDB', 'Express.js', 'React', 'Node.js'].map(skill => {
      const badgeColor = {
        MongoDB: '#47A248', // Green
        'Express.js': '#000000', // Black
        React: '#61DAFB', // Light Blue
        'Node.js': '#8CC84B', // Green
      }[skill];

      return (
        <img
          key={skill}
          src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase().replace('.', '-')}&logoColor=white`}
          alt={`${skill} logo`}
          style={{
            ...badgeStyle,
            transition: 'transform 0.2s ease, box-shadow 0.2s ease', // Smooth transition for hover effect
            margin: '10px',
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.boxShadow = '0 0 20px rgba(255, 255, 255, 0.7)'; // Bright glow on hover
            e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.boxShadow = 'none'; // Restore shadow color
            e.currentTarget.style.transform = 'scale(1)'; // Reset scale
          }}
        />
      );
    })}
  </div>
</div>


          {/* Additional Sections */}
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#f0f0f0', // Light background for contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 15px rgba(0, 0, 0, 0.5)', // Softer shadow for depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#4A4A4A', // Darker color for the title
    textAlign: 'center', 
    marginBottom: '15px', 
    textShadow: '1px 1px 2px rgba(0, 0, 0, 0.3)', // Light shadow for effect
  }}>
    Testing
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['Selenium', 'JUnit'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-43B02A?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
        alt={skill}
        style={{
          ...badgeStyle,
          margin: '10px', // Added margin for spacing between badges
          transition: 'transform 0.3s, box-shadow 0.3s', // Transition for smooth effects
        }}
        onMouseOver={(e) => {
          e.currentTarget.style.boxShadow = '0 0 15px rgba(67, 176, 40, 0.7)'; // Green glow on hover
          e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.boxShadow = 'none'; // Remove shadow on mouse out
          e.currentTarget.style.transform = 'scale(1)'; // Reset scale
        }}
      />
    ))}
  </div>
</div>




          
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#e8f0fe', // Light background for contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 15px rgba(0, 0, 0, 0.5)', // Softer shadow for depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#3D3D3D', // Darker color for the title
    textAlign: 'center', 
    marginBottom: '15px', 
    textShadow: '1px 1px 2px rgba(0, 0, 0, 0.3)', // Light shadow for effect
  }}>
    DevOps
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['Docker', 'Kubernetes', 'Jenkins', 'Terraform'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-0db7ed?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
        alt={skill}
        style={{
          ...badgeStyle,
          margin: '10px', // Added margin for spacing between badges
          transition: 'transform 0.3s, box-shadow 0.3s', // Transition for smooth effects
        }}
        onMouseOver={(e) => {
          e.currentTarget.style.boxShadow = '0 0 15px rgba(13, 183, 237, 0.7)'; // Blue glow on hover
          e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.boxShadow = 'none'; // Remove shadow on mouse out
          e.currentTarget.style.transform = 'scale(1)'; // Reset scale
        }}
      />
    ))}
  </div>
</div>







<div style={skillsSectionStyle}>
  <h3 style={sectionTitleStyle}>Tools & Platforms</h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['Git', 'GitHub', 'Docker', 'Visual_Studio_Code'].map(skill => {
      const badgeColor = {
        Git: '#F05032', // Git color (red)
        GitHub: '#181717', // GitHub color (dark gray)
        Docker: '#2496ED', // Docker color (blue)
        Visual_Studio_Code: '#007ACC', // VS Code color (blue)
      }[skill];

      return (
        <a 
          key={skill} 
          href={`https://www.${skill.toLowerCase().replace('_', '-')}.com`} // Make it a link to the tool's website
          target="_blank" 
          rel="noopener noreferrer"
          style={{ margin: '10px', display: 'inline-block' }} // Ensure proper layout and spacing
          title={`Learn more about ${skill}`} // Tooltip
        >
          <img
            src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
            alt={`${skill} logo`} // Enhanced alt text
            style={{
              ...badgeStyle,
              filter: 'brightness(1.2)', // Slightly brighter badges
              transition: 'box-shadow 0.3s, transform 0.3s', // Transition effects
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.boxShadow = '0 0 20px rgba(255, 255, 255, 0.7)'; // Bright glow on hover
              e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.boxShadow = `0 0 10px ${badgeColor}`; // Restore shadow color
              e.currentTarget.style.transform = 'scale(1)'; // Reset scale
            }}
          />
        </a>
      );
    })}
  </div>
</div>



<div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#1c1c1c', // Darker background for better contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 20px rgba(0, 0, 0, 0.7)', // Deeper shadow for more depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#ffcc00', // Bright yellow for the title
    textAlign: 'center', 
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)', // Text shadow for effect
  }}>
    Database
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['MySQL', 'PostgreSQL', 'NoSQL'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-4479A1?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
        alt={skill}
        style={{
          ...badgeStyle,
          filter: 'brightness(1.2)', // Slightly brighter badges
          transition: 'box-shadow 0.3s, transform 0.3s', 
          margin: '10px', 
          borderRadius: '10px', // More rounded corners
          cursor: 'pointer', 
          boxShadow: `0 0 10px #4479A1`, // Shadow matching badge color
        }}
        onMouseOver={(e) => {
          e.currentTarget.style.boxShadow = '0 0 20px rgba(255, 255, 255, 0.7)'; // Bright glow on hover
          e.currentTarget.style.transform = 'scale(1.1)'; // Slightly larger on hover
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.boxShadow = `0 0 10px #4479A1`; // Restore shadow color
          e.currentTarget.style.transform = 'scale(1)'; // Reset scale
        }}
      />
    ))}
  </div>
</div>






          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>Cloud & DevOps</h3>
            <div>
              {['AWS', 'Azure', 'Jenkins'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-0078D4?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
                  alt={skill}
                  style={badgeStyle}
                  onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                  onMouseOut={(e) => e.currentTarget.style = badgeStyle}
                />
              ))}
            </div>
          </div>

          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>Operating Systems</h3>
            <div>
              {['Linux', 'Unix', 'Windows', 'Unix_Shell_Scripting'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
                  alt={skill}
                  style={badgeStyle}
                  onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                  onMouseOut={(e) => e.currentTarget.style = badgeStyle}
                />
              ))}
            </div>
          </div>

          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>Frameworks</h3>
            <div>
              {['Spring_Boot', 'Hibernate', 'JDBC', 'JSP', 'Servlets'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
                  alt={skill}
                  style={badgeStyle}
                  onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                  onMouseOut={(e) => e.currentTarget.style = badgeStyle}
                />
              ))}
            </div>
          </div>

          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>Web Technologies</h3>
            <div>
              <img
                src="https://img.shields.io/badge/-RESTful_API-007396?style=flat&logo=rest&logoColor=white"
                alt="RESTful API"
                style={badgeStyle}
                onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                onMouseOut={(e) => e.currentTarget.style = badgeStyle}
              />
            </div>
          </div>
          
         

          <div style={skillsSectionStyle}>
  <h3 style={sectionTitleStyle}>Security</h3>
  <div>
    {['Spring_Security'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
        alt={skill}
        style={badgeStyle}
        onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
        onMouseOut={(e) => e.currentTarget.style = badgeStyle}
      />
    ))}
  </div>
</div>

<div style={skillsSectionStyle}>
  <h3 style={sectionTitleStyle}>Methodologies</h3>
  <div>
    {['Agile', 'Scrum'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
        alt={skill}
        style={badgeStyle}
        onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
        onMouseOut={(e) => e.currentTarget.style = badgeStyle}
      />
    ))}
  </div>
</div>

<div style={skillsSectionStyle}>
  <h3 style={sectionTitleStyle}>Microsoft .NET Technologies</h3>
  <div>
    {['ASP.NET_MVC_Core', 'NET_Core', 'ADO.NET', 'CSharp', 'Web_API', 'Entity_Framework'].map(skill => (
      <img
        key={skill}
        src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase().replace('_', '-')}&logoColor=white`}
        alt={skill}
        style={badgeStyle}
        onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
        onMouseOut={(e) => e.currentTarget.style = badgeStyle}
      />
    ))}
  </div>
</div>



          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>PROJECT MANAGEMENT TOOLS</h3>
            <div>
              {['JIRA', 'Postman'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
                  alt={skill}
                  style={badgeStyle}
                  onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                  onMouseOut={(e) => e.currentTarget.style = badgeStyle}
                />
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
