import React from 'react';
import frontendImg from "../../assets/images/front-end.png";

const Services = () => {
  return (
    <section id="services" style={styles.section}>
      <div className="container lg:pt-5">
        <div className="text-center">
          <h2 className="text-smallTextColor font-bold text-2xl mb-5">Education</h2>
        </div>

        {/* Timeline container */}
        <div style={styles.timelineContainer}>
          {/* Vertical Line */}
          <div style={styles.verticalLine}></div>

          {/* Timeline Content */}
          <div style={styles.timelineContent}>

            {/* CDAC Card */}
            <TimelineCard
              title="CDAC"
              icon="ri-graduation-cap-fill"
              description="PG-DAC, IACSD, Pune"
              date="March 2024 – August 2024"
              imageSrc={frontendImg}
              animation="fade-left"
            />

            {/* Internship Card */}
            <TimelineCard
              title="Internship"
              icon="ri-robot-2-fill"
              description="Machine Learning Intern, Aiverient, Pune"
              date="June 2022 – February 2023"
              imageSrc={frontendImg}
              animation="fade-right"
            />

            {/* B.Tech Card */}
            <TimelineCard
              title="B.Tech"
              icon="ri-graduation-cap-fill"
              description="Mechanical Engineering, DKTE Textile and Engineering Institute, Ichalkaranji"
              date="2016 – 2020"
              additionalInfo="7.78 CGPA"
              imageSrc={frontendImg}
              animation="fade-left"
            />

            {/* HSC Card */}
            <TimelineCard
              title="HSC"
              description="A.K. Junior College, Atpadi"
              date="2014 – 2016"
              additionalInfo="67.54%"
              imageSrc={frontendImg}
              animation="fade-right"
            />

            {/* SSC Card */}
            <TimelineCard
              title="SSC"
              icon="ri-school-fill"
              description="Shree Bhavnai Vidyalaya, Atpadi"
              date="2014"
              additionalInfo="90.60%"
              imageSrc={frontendImg}
              animation="fade-left"
            />
          </div>
        </div>

        {/* Projects Section */}
        <div className="text-center mt-12">
          <h2 className="text-smallTextColor font-bold text-2xl mb-5">Projects</h2>
        </div>

        {/* Projects */}
        <div style={styles.projectsContainer}>
          {/* Project 1 - AgroMart */}
          <ProjectCard
            title="AgroMaart - Online Agriculture Marketplace"
            description="AgroMaart is an online platform designed to connect farmers, buyers, and delivery personnel in a seamless agricultural marketplace. It allows users to browse, purchase, and manage agricultural products and services. Features include real-time order tracking, secure payment processing, location services, and communication through SMS and notifications."
            technologies="React, HTML, CSS, Spring Boot, Hibernate, MySQL"
            imageSrc={frontendImg}
            animation="fade-right"
          />

          {/* Project 2 - Music Recommendation System */}
          <ProjectCard
            title="Music Recommendation System"
            description="A personalized music recommendation system leveraging user preferences and listening history."
            technologies="Python, Flask, TensorFlow, Spotify API"
            imageSrc={frontendImg}
            animation="fade-left"
          />

          {/* Project 3 - Drug Review Using NLP */}
          <ProjectCard
            title="Drug Review Using NLP"
            description="An analysis tool to interpret and categorize drug reviews using natural language processing."
            technologies="Python, Flask, NLP, TensorFlow"
            imageSrc={frontendImg}
            animation="fade-right"
          />
        </div>
      </div>
    </section>
  );
}

/* Timeline Card Component */
const TimelineCard = ({ title, icon, description, date, additionalInfo, imageSrc, animation }) => (
  <div style={styles.cardContainer}>
    <div style={styles.cardContent}>
      <div
        data-aos={animation}
        data-aos-duration="1200"
        style={styles.card}
      >
        <h3 style={styles.cardTitle}>
          {icon && <i className={`${icon} mr-2`}></i>} {title}
        </h3>
        <p style={styles.cardDescription}>{description}</p>
        {date && <p style={styles.cardDate}>{date}</p>}
        {additionalInfo && <p style={styles.cardAdditionalInfo}>{additionalInfo}</p>}
      </div>
    </div>
    <div style={styles.iconContainer}>
      <img src={imageSrc} alt={`Icon for ${title}`} style={styles.iconImage} />
    </div>
  </div>
)

/* Project Card Component */
const ProjectCard = ({ title, description, technologies, imageSrc, animation }) => (
  <div style={styles.cardContainer}>
    <div style={styles.cardContent}>
      <div
        data-aos={animation}
        data-aos-duration="1200"
        style={styles.card}
      >
        <h3 style={styles.cardTitle}>{title}</h3>
        <p style={styles.cardDescription}>{description}</p>
        <p style={styles.cardTechnologies}><b>Technologies:</b> {technologies}</p>
      </div>
    </div>
    <div style={styles.iconContainer}>
      <img src={imageSrc} alt={`Icon for ${title}`} style={styles.iconImage} />
    </div>
  </div>
)

/* CSS-in-JS Styles */
const styles = {
  section: {
    padding: '2rem',
    backgroundColor: '#f8f9fa',
  },
  timelineContainer: {
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  verticalLine: {
    position: 'absolute',
    width: '2px',
    height: '100%',
    backgroundColor: '#4f46e5',
    left: '50%',
    transform: 'translateX(-50%)',
  },
  timelineContent: {
    marginTop: '3rem',
    width: '100%',
    maxWidth: '1200px',
    padding: '0 1rem',
  },
  projectsContainer: {
    marginTop: '3rem',
    width: '100%',
    maxWidth: '1200px',
    padding: '0 1rem',
  },
  cardContainer: {
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '1.5rem',
  },
  cardContent: {
    display: 'flex',
    justifyContent: 'flex-start',
    width: '100%',
    maxWidth: '800px',
    padding: '0 1rem',
  },
  card: {
    backgroundColor: '#ffffff',
    padding: '1rem',
    borderRadius: '0.5rem',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'background-color 0.2s ease-in',
    cursor: 'pointer',
  },
  cardTitle: {
    color: '#4f46e5',
    fontWeight: 'bold',
    marginBottom: '0.5rem',
    fontSize: '1.25rem',
  },
  cardDescription: {
    fontSize: '0.875rem',
    color: '#333',
  },
  cardDate: {
    fontSize: '0.875rem',
    color: '#666',
    marginTop: '0.5rem',
  },
  cardAdditionalInfo: {
    fontSize: '0.875rem',
    color: '#666',
    marginTop: '0.5rem',
  },
  cardTechnologies: {
    fontSize: '0.875rem',
    color: '#666',
    marginTop: '0.5rem',
  },
  iconContainer: {
    position: 'absolute',
    backgroundColor: '#4f46e5',
    border: '2px solid #ffffff',
    borderRadius: '50%',
    width: '40px',
    height: '40px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    top: '-20px',
  },
  iconImage: {
    width: '24px',
    height: '24px',
  },
};

export default Services;
