import React from 'react';
import PropTypes from 'prop-types';

const Project = () => {
  // Sample project data to prevent map error
  const projectData = [
    {
      title: "AgroMaart - Online Agriculture Marketplace",
      icon: "ri-shopping-cart-fill",
      description: `AgroMaart is a web-based application designed to enhance the efficiency of fertilizer distribution by connecting shop owners, farmers, and delivery personnel on a single platform. Developed using React, HTML, CSS, and Bootstrap for the frontend, and Spring Boot, Spring Security, Hibernate, and MySQL for the backend, the application streamlines communication and management processes within the agricultural sector.
  
      Key Features:
      - **Role-Based Access:** Implements a role-based system, allowing specific functionalities for shop owners, farmers, and delivery personnel.
      - **Inventory and Order Management:** Enables shop owners to manage inventory and orders, while farmers can browse products, check prices, place orders, and track deliveries.
      - **Real-Time Updates:** Delivery personnel can update the status of deliveries in real-time, fostering transparency and improving communication among all stakeholders.
      - **Data Security:** Utilizes Spring Security for secure user authentication and data protection.
  
      Launched in 2023, AgroMaart significantly reduces manual effort and enhances operational transparency, making the fertilizer distribution process more efficient for all involved. For more details, visit the [GitHub Repository](https://github.com/Akshaybalte1998/AgroMart-Project.git).`,
      date: "2023",
      additionalInfo: "Technologies: React, HTML, CSS, Spring Boot, Hibernate, MySQL",
      imageSrc: require('../../assets/images/AgroMart.webp'),
      animation: "fade-left",
    },
    {
      title: "Music Recommendation System",
      icon: "ri-music-fill",
      description: `Developed a music recommendation system using Python to enhance user experience through personalized song suggestions. 
  
      Key Features:
      - **Content-Based Filtering:** Recommended songs based on features such as genre and tempo using techniques like cosine similarity.
      - **Collaborative Filtering:** Analyzed user preferences and generated recommendations based on the behaviors of similar users.
      - **Data Processing:** Processed and cleaned large datasets, extracted features, and performed data normalization to improve recommendation accuracy.
      - **Libraries Used:** Employed libraries such as Pandas for data manipulation, Scikit-learn for machine learning algorithms, and NumPy for numerical operations.
      - **Continuous Improvement:** Integrated user interaction data to continually refine and improve recommendations over time.`,
      date: "2023",
      additionalInfo: "Technologies: Python, Pandas, Scikit-learn, NumPy",
      imageSrc: require('../../assets/images/Music.webp'), // Add the path to your image
      animation: "fade-right", // Change animation as needed
    },
    {
      title: "Drug Review Analysis Using NLP",
      icon: "ri-file-text-fill",
      description: `Developed a natural language processing (NLP) system to analyze and classify drug reviews, enabling insights into user experiences and drug efficacy.
  
      Key Features:
      - **Data Collection:** Gathered a large dataset of drug reviews from online platforms, ensuring diversity and comprehensiveness of data.
      - **Text Preprocessing:** Cleaned and preprocessed text data by removing stop words, punctuation, and applying tokenization and stemming/lemmatization techniques.
      - **Sentiment Analysis:** Implemented sentiment analysis using libraries like NLTK and TextBlob to classify reviews as positive, negative, or neutral based on user sentiment.
      - **Feature Extraction:** Utilized techniques such as TF-IDF and word embeddings (e.g., Word2Vec, GloVe) to convert text into numerical format for machine learning models.
      - **Model Development:** Trained classification models (e.g., Logistic Regression, Random Forest, LSTM) to predict sentiment and effectiveness ratings, achieving an accuracy of over 85%.
      - **Visualization:** Created visualizations using Matplotlib and Seaborn to present insights, trends, and patterns in drug reviews, aiding in data-driven decision-making.
      - **Deployment:** Developed a user-friendly web application to allow users to submit reviews and receive immediate sentiment analysis results.`,
      date: "2023",
      additionalInfo: "Technologies: Python, NLTK, TextBlob, Scikit-learn, Matplotlib, Seaborn",
      imageSrc: require('../../assets/images/drug.webp'), // Add the path to your image
      animation: "fade-up", // Change animation as needed
    },
  ];
  

  return (
    <section id="project" style={styles.section}>
      <div style={styles.container}>
        {/* Projects Section Header */}
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Projects</h2>
        </div>

        {/* Projects Timeline */}
        <div style={styles.timelineContainer}>
  <div style={styles.timelineContent}>
    {projectData.map((project, index) => (  // Change 'projects' to 'projectData'
      <TimelineCard
        key={index}
        {...project} // Destructured props for cleaner code
      />
    ))}
  </div>
</div>
      </div>
    </section>
  );
};

// Timeline Card Component
const TimelineCard = ({ title, icon, description, date, additionalInfo, imageSrc, animation }) => (
  <div style={styles.cardContainer}>
    <div style={styles.iconContainer}>
      <img src={imageSrc} alt={`Icon for ${title}`} style={styles.iconImage} />
    </div>
    <div style={styles.cardContent}>
      <div data-aos={animation} data-aos-duration="1200" style={styles.timelineCard}>
        <h3 style={styles.cardTitle}>
          {icon && <i className={`${icon} mr-2`} aria-hidden="true"></i>} {title}
        </h3>
        <p style={styles.cardDescription}>{description}</p>
        {date && <p style={styles.cardDate}>{date}</p>}
        {additionalInfo && <p style={styles.cardAdditionalInfo}>{additionalInfo}</p>}
      </div>
    </div>
  </div>
);

// PropTypes for TimelineCard Component
TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  date: PropTypes.string,
  additionalInfo: PropTypes.string,
  imageSrc: PropTypes.string.isRequired,
  animation: PropTypes.string.isRequired,
};

// CSS-in-JS Styles with improved readability
const styles = {
  section: {
    padding: '4rem 2rem',
    background: 'linear-gradient(135deg, #e0f7fa, #f1f8e9)',
    borderRadius: '0.75rem',
    boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '0 1rem',
  },
  sectionHeader: {
    textAlign: 'center',
    marginBottom: '3rem',
  },
  sectionTitle: {
    color: '#00796b',
    fontWeight: 'bold',
    fontSize: '2.5rem',
    marginBottom: '0.5rem',
    textTransform: 'uppercase',
    letterSpacing: '1.5px',
  },
  timelineContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '2rem',
    padding: '2rem',
    backgroundColor: '#ffffff',
    borderRadius: '0.75rem',
    boxShadow: '0 4px 16px rgba(0, 0, 0, 0.05)',
  },
  timelineContent: {
    display: 'flex',
    flexDirection: 'column',
    gap: '2rem',
  },
  cardContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '1.5rem',
    backgroundColor: '#e1f5fe',
    padding: '1.5rem',
    borderRadius: '0.75rem',
    transition: 'transform 0.3s ease',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  cardContent: {
    flex: 1,
  },
  timelineCard: {
    padding: '1.25rem',
    borderRadius: '0.75rem',
    backgroundColor: '#b2ebf2',
  },
  iconContainer: {
    flexShrink: 0,
  },
  iconImage: {
    width: '70px',
    height: '70px',
    objectFit: 'cover',
    borderRadius: '50%',
  },
  cardTitle: {
    fontWeight: 'bold',
    fontSize: '1.6rem',
    color: '#00796b',
  },
  cardDescription: {
    fontSize: '1rem',
    color: '#37474f',
    marginTop: '0.5rem',
  },
  cardDate: {
    fontStyle: 'italic',
    color: '#5d4037',
    marginTop: '0.25rem',
  },
  cardAdditionalInfo: {
    fontSize: '0.9rem',
    color: '#4e342e',
    marginTop: '0.25rem',
  },
};

export default Project;
