import React from 'react';
import PropTypes from 'prop-types';

const Project = () => {
  const projects = [
    { title: "AgroMaart - Online Fertilizer Shop", icon: "ri-shopping-cart-fill", description: "Built a user-friendly web application to make fertilizer shop management easier and improve communication between shop owners, farmers, and delivery staff. The app includes Role-Based Access, giving each user group specific tools.", additionalInfo: "Technologies: Spring Boot, Hibernate, MySQL, React, HTML, CSS", imageSrc: require('../../assets/images/AgroMart.webp'), animation: "fade-left" },
    { title: "Students Management System", icon: "ri-book-2-fill", description: "A web-based application built with Spring Boot, Thymeleaf, and MySQL to manage student records, including enrollment, updates, and retrieval of student data.", additionalInfo: "Technologies: Spring Boot, Thymeleaf, MySQL", imageSrc: require('../../assets/images/studentManagementSystem.webp'), animation: "fade-left" },
    { title: "Employee Management System", icon: "ri-user-3-fill", description: "A Java-based system utilizing MySQL and Hibernate for efficiently managing employee details, roles, and salary records with CRUD operations.", additionalInfo: "Technologies: Java, MySQL, Hibernate", animation: "fade-left" },
    { title: "Akshay Balte Portfolio", icon: "ri-portfolio-fill", description: "A portfolio website built with React, JavaScript, HTML, and CSS showcasing skills, projects, and achievements.", additionalInfo: "Technologies: React, JavaScript, HTML, CSS", animation: "fade-left" },
  ];

  return (
    <section id="project" style={styles.section}>
      <div style={styles.container}>
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Projects</h2>
        </div>
        <div style={styles.timelineContent}>
          {projects.reduce((rows, project, index) => {
            if (index % 2 === 0) {
              rows.push([project]);
            } else {
              rows[rows.length - 1].push(project);
            }
            return rows;
          }, []).map((pair, index) => (
            <div key={index} style={styles.row}>
              {pair.map((proj, idx) => (
                <TimelineCard key={idx} {...proj} />
              ))}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

const TimelineCard = ({ title, icon, description, additionalInfo, imageSrc, animation }) => {
  return (
    <div className="timeline-card" style={styles.cardContainer} data-aos={animation}>
      <div style={styles.cardContent}>
        <h3 style={styles.cardTitle}><i className={`${icon} mr-2`}></i> {title}</h3>
        <p style={styles.cardDescription}>{description}</p>
        {additionalInfo && <p style={styles.cardAdditionalInfo}>{additionalInfo}</p>}
      </div>
    </div>
  );
};

TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  additionalInfo: PropTypes.string,
  animation: PropTypes.string.isRequired,
};

const styles = {
  section: { padding: '4rem 2rem', background: '#f5f5f5', borderRadius: '1rem' },
  container: { maxWidth: '1100px', margin: '0 auto' },
  sectionHeader: { textAlign: 'center', marginBottom: '3rem' },
  sectionTitle: { color: '#0288d1', fontSize: '2.8rem', textTransform: 'uppercase' },
  timelineContent: { display: 'flex', flexDirection: 'column', gap: '2rem' },
  row: { display: 'flex', justifyContent: 'space-between', gap: '2rem' },
  cardContainer: { flex: 1, padding: '1.5rem', borderRadius: '1rem', boxShadow: '0 6px 16px rgba(0, 0, 0, 0.1)' },
  cardContent: { flex: 1 },
  cardTitle: { fontSize: '1.8rem', color: '#0277bd' },
  cardDescription: { fontSize: '1rem', color: '#455a64' },
  cardAdditionalInfo: { fontSize: '0.9rem', color: '#607d8b', fontStyle: 'italic' },
};

export default Project;
