import React from 'react';
import PropTypes from 'prop-types';
import './Project.css';

const Project = () => {
  const projects = [
    {
      title: "AgroMaart - Online Fertilizer Shop",
      icon: "ri-shopping-cart-fill",
      description: "Built a user-friendly web application to make fertilizer shop management easier and improve communication between shop owners, farmers, and delivery staff...",
      additionalInfo: "Technologies: Spring Boot, Hibernate, MySQL React, HTML, CSS",
      imageSrc: require('../../assets/images/AgroMart.webp'),
      animation: "fade-left",
    },
    {
      title: "Students Management System",
      icon: "ri-book-2-fill",
      description: "The Students Management System is a web-based application designed to manage student records efficiently...",
      additionalInfo: "Technologies: Spring Boot, Hibernate, Thymeleaf, MySQL",
      imageSrc: require('../../assets/images/studentManagementSystem.webp'),
      animation: "fade-left",
    },
  ];

  return (
    <section id="project" className="project-section">
      <div className="project-container">
        <div className="section-header">
          <h2 className="section-title">Projects</h2>
        </div>
        <div className="timeline-content">
          {projects.map((project, index) => (
            <TimelineCard key={index} index={index} {...project} />
          ))}
        </div>
      </div>
    </section>
  );
};

const TimelineCard = ({ title, icon, description, additionalInfo, imageSrc, animation, index }) => {
  return (
    <div className="timeline-card" data-aos={animation}>
      <div className="icon-container">
        <img src={imageSrc} alt={`Icon for ${title}`} className="icon-image" />
      </div>
      <div className="card-content">
        <h3 className="card-title">
          {icon && <i className={`${icon} mr-2`} aria-hidden="true"></i>} {title}
        </h3>
        <p className="card-description">{description}</p>
        {additionalInfo && <p className="card-additional-info">{additionalInfo}</p>}
      </div>
    </div>
  );
};

TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  additionalInfo: PropTypes.string,
  imageSrc: PropTypes.string.isRequired,
  animation: PropTypes.string.isRequired,
  index: PropTypes.number.isRequired,
};

const styles = `
  .project-section {
    padding: 4rem 2rem;
    background: linear-gradient(135deg, #4facfe, #00f2fe);
    border-radius: 1rem;
    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
  }
  .project-container {
    max-width: 1100px;
    margin: 0 auto;
    padding: 1rem;
  }
  .section-header {
    text-align: center;
    margin-bottom: 3rem;
  }
  .section-title {
    color: #fff;
    font-weight: 700;
    font-size: 2.8rem;
    margin-bottom: 1rem;
    letter-spacing: 1.5px;
    text-transform: uppercase;
  }
  .timeline-content {
    display: flex;
    flex-wrap: wrap;
    gap: 2rem;
    justify-content: center;
  }
  .timeline-card {
    display: flex;
    align-items: center;
    gap: 1.5rem;
    padding: 1.5rem;
    border-radius: 1rem;
    box-shadow: 0 6px 16px rgba(0, 0, 0, 0.1);
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }
  .timeline-card:hover {
    transform: scale(1.03);
    box-shadow: 0 8px 18px rgba(0, 0, 0, 0.15);
  }
  .icon-container img {
    width: 80px;
    height: 80px;
    object-fit: cover;
    border-radius: 50%;
  }
  .card-content {
    flex: 1;
    color: #fff;
  }
  .card-title {
    font-weight: 700;
    font-size: 1.8rem;
  }
  .card-description {
    font-size: 1rem;
    margin-top: 0.75rem;
  }
  .card-additional-info {
    font-size: 0.9rem;
    margin-top: 0.5rem;
    font-style: italic;
  }
`;

const styleTag = document.createElement("style");
styleTag.innerHTML = styles;
document.head.appendChild(styleTag);

export default Project;
