// Importing images
import React from 'react';
import portfolioImg01 from "../images/portfolio-01.jpg";
import portfolioImg02 from "../images/portfolio-02.jpg";
import portfolioImg03 from "../images/portfolio-03.jpg";
import portfolioImg04 from "../images/portfolio-04.jpg";

// Inline styles
const styles = {
  portfolioContainer: {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px',
    justifyContent: 'center',
  },
  portfolioItem: {
    background: '#fff',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    overflow: 'hidden',
    width: '300px',
    transition: 'transform 0.3s, box-shadow 0.3s',
  },
  portfolioItemHover: {
    transform: 'translateY(-10px)',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
  },
  portfolioImg: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  portfolioContent: {
    padding: '15px',
  },
  portfolioTitle: {
    fontSize: '1.2em',
    margin: '0',
  },
  portfolioCategory: {
    fontSize: '0.9em',
    color: '#888',
  },
  portfolioDescription: {
    margin: '10px 0',
    color: '#555',
  },
  portfolioTechnologies: {
    fontSize: '0.9em',
    color: '#007BFF',
  },
  portfolioLink: {
    display: 'inline-block',
    marginTop: '10px',
    color: '#007BFF',
    textDecoration: 'none',
  },
  portfolioLinkHover: {
    textDecoration: 'underline',
  },
};

// Portfolio data
const portfolios = [
  {
    id: "01",
    imgUrl: portfolioImg01,
    category: "Internship",
    title: "Aivarient",
    description: "Completed internship in machine learning using Python by Aivarient",
    technologies: ["Machine Learning", "Python"],
    siteUrl: "#",
  },
  {
    id: "02",
    imgUrl: portfolioImg02,
    category: "Pre-CAT",
    title: "Sunbeam Institute",
    description: "Completed a course of Pre-CAT for CDAC by Sunbeam Institute",
    technologies: ["C", "C++", "Data Structure"],
    siteUrl: "#",
  },
  {
    id: "03",
    imgUrl: portfolioImg03,
    category: "Certification",
    title: "Machine Learning with Python by IBM",
    description: "Successfully completed the certification by IBM in Machine Learning with Python",
    technologies: ["Python", "Machine Learning"],
    siteUrl: "#",
  },
  {
    id: "04",
    imgUrl: portfolioImg04,
    category: "UX",
    title: "SQL",
    description: "Successfully completed the course on MySQL as taught by Great Learning",
    technologies: ["SQL"],
    siteUrl: "#",
  },
];

// React Component
const Portfolio = () => {
  return (
    <div style={styles.portfolioContainer}>
      {portfolios.map((portfolio) => (
        <div
          key={portfolio.id}
          style={styles.portfolioItem}
          onMouseOver={(e) => e.currentTarget.style = {...styles.portfolioItem, ...styles.portfolioItemHover}}
          onMouseOut={(e) => e.currentTarget.style = styles.portfolioItem}
        >
          <img src={portfolio.imgUrl} alt={portfolio.title} style={styles.portfolioImg} />
          <div style={styles.portfolioContent}>
            <h2 style={styles.portfolioTitle}>{portfolio.title}</h2>
            <p style={styles.portfolioCategory}>{portfolio.category}</p>
            <p style={styles.portfolioDescription}>{portfolio.description}</p>
            <p style={styles.portfolioTechnologies}>{portfolio.technologies.join(', ')}</p>
            <a href={portfolio.siteUrl} style={styles.portfolioLink} onMouseOver={(e) => e.currentTarget.style = styles.portfolioLinkHover} onMouseOut={(e) => e.currentTarget.style = styles.portfolioLink}>Learn More</a>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Portfolio;
