import React from 'react';

// Define the styles
const containerStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
  gap: '1.5rem',
  padding: '2rem',
};

const skillsSectionStyle = {
  background: 'linear-gradient(145deg, #ffffff, #f5f5f5)',
  borderRadius: '12px',
  boxShadow: '0px 10px 20px rgba(0, 0, 0, 0.1)',
  padding: '1.5rem',
  textAlign: 'center',
};

const sectionTitleStyle = {
  fontSize: '1.8rem',
  fontWeight: '600',
  marginBottom: '1rem',
  color: '#333',
};

const badgeStyle = {
  margin: '5px',
  display: 'inline-block',
  transition: 'transform 0.3s, box-shadow 0.3s',
};

const badgeHoverStyle = {
  ...badgeStyle,
  transform: 'scale(1.1)',
  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
};

const Skills = () => {
  return (
    <section id="Skills" style={{ padding: '4rem', backgroundColor: '#e8f5e9' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <h2 style={{ textAlign: 'center', fontSize: '2.5rem', fontWeight: '700', marginBottom: '2rem', color: '#007396' }}>
        Skills & Expertise
        </h2>

        <div style={containerStyle}>
          {/* Frontend Section */}
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#2b2b2b', // Dark grey background for a softer look
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 20px rgba(0, 0, 0, 0.5)', // Added shadow for depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#f0f0f0', // Light grey text for better readability
    textAlign: 'center', // Center the title
  }}>
    Frontend
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}> {/* Flexbox for responsive layout */}
    {['JavaScript', 'HTML5', 'CSS3', 'React'].map(skill => {
      const badgeColor = {
        JavaScript: '#f7df1e',
        HTML5: '#e34f26',
        CSS3: '#1572b6',
        React: '#61dafb',
      }[skill];

      return (
        <img
          key={skill}
          src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase()}&logoColor=black`}
          alt={skill}
          style={{
            ...badgeStyle,
            filter: 'brightness(1.1)', // Slightly brighter badges
            transition: 'box-shadow 0.3s, transform 0.3s', // Transition for shadow and scale effects
            margin: '10px', // Add margin between badges
            borderRadius: '5px', // Rounded corners for badges
            cursor: 'pointer', // Change cursor to pointer on hover
          }}
          onMouseOver={(e) => {
            e.currentTarget.style.boxShadow = '0 0 15px rgba(255, 255, 255, 0.7)'; // Glow on hover
            e.currentTarget.style.transform = 'scale(1.05)'; // Slightly scale up on hover
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.boxShadow = 'none'; // Remove glow effect
            e.currentTarget.style.transform = 'scale(1)'; // Reset scale
          }}
        />
      );
    })}
  </div>
</div>



          {/* Backend Section */}
          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>Backend</h3>
            <div>
              {['Java', 'Node.js', 'CSharp', 'C++'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-007396?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
                  alt={skill}
                  style={badgeStyle}
                  onMouseOver={(e) => e.currentTarget.style = badgeHoverStyle}
                  onMouseOut={(e) => e.currentTarget.style = badgeStyle}
                />
              ))}
            </div>
          </div>

          {/* MERN Stack Section */}
          <div style={skillsSectionStyle}>
            <h3 style={sectionTitleStyle}>MERN Stack</h3>
            <div>
              {['MongoDB', 'Express.js', 'React', 'Node.js'].map(skill => (
                <img
                  key={skill}
                  src={`https://img.shields.io/badge/-${skill}-47A248?style=flat&logo=${skill.toLowerCase()}&logoColor=white`}
