import React from 'react';
import PropTypes from 'prop-types';
import frontendImg from "../../assets/images/front-end.png";

// Main Services Component
const Services = () => {
  return (
    <section id="services" style={styles.section}>
      <div style={styles.container}>
        {/* Education Section */}
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Educational Journey</h2>
        </div>

        <div style={styles.timelineContainer}>
          <div style={styles.timelineLine}></div>

          <div style={styles.timelineContent}>
            <TimelineCard 
              title="CDAC"
              icon="ri-graduation-cap-fill"
              description="PG-DAC, IACSD, Pune"
              date="March 2024 – August 2024"
              additionalInfo="60.8%"
              imageSrc={require('../../assets/images/cdac_logo.png')}
              animation="fade-left"
            />

            <TimelineCard
              title="Internship"
              icon="ri-robot-2-fill"
              description="Machine Learning Intern, Aiverient, Pune"
              date="June 2022 – February 2023"
              imageSrc={require('../../assets/images/Aivarient.png')}
              animation="fade-right"
            />
            <TimelineCard
              title="B.Tech Mechanical Engineering"
              icon="ri-graduation-cap-fill"
              description="DKTE Textile and Engineering Institute, Ichalkaranji"
              date="2016 – 2020"
              additionalInfo="7.78 CGPA"
              imageSrc={require('../../assets/images/btech.png')}
              animation="fade-left"
            />
            <TimelineCard
              title="HSC"
              icon="ri-school-fill"
              description="A.K. Junior College, Atpadi"
              date="2014 – 2016"
              additionalInfo="67.54%"
              imageSrc={require('../../assets/images/hsc.png')}
              animation="fade-right"
            />
            <TimelineCard
              title="SSC"
              icon="ri-school-fill"
              description="Shree Bhavnai Vidyalaya, Atpadi"
              date="2014"
              additionalInfo="90.60%"
              imageSrc={require('../../assets/images/ssc.png')}
              animation="fade-left"
            />
          </div>
        </div>

        {/* Projects Section */}
        <div style={styles.sectionHeader}>
          <h2 style={styles.sectionTitle}>Projects</h2>
        </div>

        <div style={styles.projectsContainer}>
          <ProjectCard
            title="AgroMaart - Online Agriculture Marketplace"
            description="AgroMaart is an online platform designed to connect farmers, buyers, and delivery personnel in a seamless agricultural marketplace."
            technologies="React, HTML, CSS, Spring Boot, Hibernate, MySQL"
            imageSrc={require('../../assets/images/AgroMart.webp')}
            animation="fade-right"
          />
          <ProjectCard
            title="Music Recommendation System"
            description="A personalized music recommendation system leveraging user preferences and listening history."
            technologies="Python, Flask, TensorFlow, Spotify API"
            imageSrc={require('../../assets/images/Music.webp')}
            animation="fade-left"
          />
          <ProjectCard
            title="Drug Review Using NLP"
            description="An analysis tool to interpret and categorize drug reviews using natural language processing."
            technologies="Python, Flask, NLP, TensorFlow"
            imageSrc={require('../../assets/images/drug.webp')}
            animation="fade-right"
          />
        </div>
      </div>
    </section>
  );
};

// Timeline Card Component
const TimelineCard = ({ title, icon, description, date, additionalInfo, imageSrc, animation }) => (
  <div style={styles.cardContainer}>
    <div style={styles.iconContainer}>
      <img src={imageSrc} alt={`Icon for ${title}`} style={styles.iconImage} />
    </div>
    <div style={styles.cardContent}>
      <div data-aos={animation} data-aos-duration="1200" style={styles.timelineCard}>
        <h3 style={styles.cardTitle}>
          {icon && <i className={`${icon} mr-2`}></i>} {title}
        </h3>
        <p style={styles.cardDescription}>{description}</p>
        {date && <p style={styles.cardDate}>{date}</p>}
        {additionalInfo && <p style={styles.cardAdditionalInfo}>{additionalInfo}</p>}
      </div>
    </div>
  </div>
);

// Project Card Component
const ProjectCard = ({ title, description, technologies, imageSrc, animation }) => (
  <div style={styles.projectCardContainer}>
    <div style={styles.projectCardContent}>
      <div data-aos={animation} data-aos-duration="1200" style={styles.projectCard}>
        <h3 style={styles.cardTitle}>{title}</h3>
        <p style={styles.cardDescription}>{description}</p>
        <p style={styles.cardTechnologies}><b>Technologies:</b> {technologies}</p>
      </div>
    </div>
    <div style={styles.iconContainer}>
      <img src={imageSrc} alt={`Icon for ${title}`} style={styles.iconImage} />
    </div>
  </div>
);

// Prop types for TimelineCard and ProjectCard components
TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  date: PropTypes.string,
  additionalInfo: PropTypes.string,
  imageSrc: PropTypes.string.isRequired,
  animation: PropTypes.string.isRequired,
};
ProjectCard.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  technologies: PropTypes.string.isRequired,
  imageSrc: PropTypes.string.isRequired,
  animation: PropTypes.string.isRequired,
};

// CSS-in-JS Styles
const styles = {
  section: {
    padding: '4rem 2rem',
    background: 'linear-gradient(135deg, #B3E5FC 30%, #e0f7fa 90%)',
    borderRadius: '0.5rem',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
  },
  sectionHeader: {
    textAlign: 'center',
    marginBottom: '2rem',
  },
  sectionTitle: {
    color: '#ff5722',
    fontWeight: 'bold',
    fontSize: '2.5rem',
    marginBottom: '1rem',
    textTransform: 'uppercase',
    letterSpacing: '1px',
  },
  timelineContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem',
    backgroundColor: '#e0f7fa',
    padding: '2rem',
    borderRadius: '0.5rem',
  },
  timelineLine: {
    width: '4px',
    background: '#ff5722',
    left: '50%',
  },
  timelineContent: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem',
  },
  projectsContainer: {
    marginTop: '3rem',
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '1.5rem',
    backgroundColor: '#B2EBF2',
    padding: '2rem',
    borderRadius: '0.5rem',
  },
  cardContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    backgroundColor: '#ffecb3',
    padding: '1rem',
    borderRadius: '0.5rem',
  },
  cardContent: {
    flex: 1,
  },
  timelineCard: {
    padding: '1rem',
    borderRadius: '0.5rem',
    backgroundColor: '#FFE4E1',
  },
  projectCardContainer: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '1rem',
  },
  projectCardContent: {
    flex: 1,
  },
  projectCard: {
    padding: '1rem',
    borderRadius: '0.5rem',
    backgroundColor: '#ffe0b2',
  },
  iconContainer: {
    flexShrink: 0,
  },
  iconImage: {
    width: '60px',
    height: '60px',
    objectFit: 'contain',
  },
  cardTitle: {
    fontWeight: 'bold',
    fontSize: '1.4rem',
    color: '#bf360c',
  },
  cardDescription: {
    fontSize: '1rem',
    color: '#4b5563',
  },
  cardDate: {
    fontStyle: 'italic',
    color: '#8d6e63',
  },
  cardAdditionalInfo: {
    fontSize: '0.9rem',
    color: '#8d6e63',
  },
  cardTechnologies: {
    fontSize: '1rem',
    color: '#4b5563',
  },
};

export default Services;
