import React from 'react';

// Define the styles
const containerStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
  gap: '1.5rem',
  padding: '2rem',
};

const skillsSectionStyle = {
  background: 'linear-gradient(145deg, #ffffff, #f5f5f5)',
  borderRadius: '12px',
  boxShadow: '0px 10px 20px rgba(0, 0, 0, 0.1)',
  padding: '1.5rem',
  textAlign: 'center',
};

const sectionTitleStyle = {
  fontSize: '1.8rem',
  fontWeight: '600',
  marginBottom: '1rem',
  color: '#333',
};

const badgeStyle = {
  margin: '5px',
  display: 'inline-block',
  transition: 'transform 0.3s, box-shadow 0.3s',
};

const badgeHoverStyle = {
  ...badgeStyle,
  transform: 'scale(1.1)',
  boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
};

const Skills = () => {
  return (
    <section id="Skills" style={{ padding: '4rem', backgroundColor: '#e8f5e9' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <h2 style={{ textAlign: 'center', fontSize: '2.5rem', fontWeight: '700', marginBottom: '2rem', color: '#007396' }}>
        Skills & Expertise
        </h2>

        <div style={containerStyle}>
          {/* Frontend Section */}
          <div style={{ 
  ...skillsSectionStyle, 
  backgroundColor: '#1c1c1c', // Darker background for better contrast
  padding: '20px', 
  borderRadius: '10px', 
  boxShadow: '0 0 20px rgba(0, 0, 0, 0.7)', // Deeper shadow for more depth
}}>
  <h3 style={{ 
    ...sectionTitleStyle, 
    color: '#ffcc00', // Bright yellow for the title
    textAlign: 'center', 
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)', // Text shadow for effect
  }}>
    Frontend
  </h3>
  <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
    {['JavaScript', 'HTML5', 'CSS3', 'React'].map(skill => {
      const badgeColor = {
        JavaScript: '#f7df1e', // Yellow
        HTML5: '#e34f26', // Red
        CSS3: '#1572b6', // Blue
        React: '#61dafb', // Light Blue
      }[skill];

      return (
        <img
          key={skill}
          src={`https://img.shields.io/badge/-${skill}-${badgeColor.slice(1)}?style=flat&logo=${skill.toLowerCase()}&logoColor=black`}
          alt={skill}
          style={{
            ...badgeStyle,
            filter: 'brightness(1.2)', // Slightly brighter badges
            transition: 'box-shadow 0.3s, transform 0.3s', 
            margin: '10px', 
            borderRadius: '10px', // More rounded corners
            cursor: 'pointer', 
