import React from 'react';
import PropTypes from 'prop-types';
import "aos/dist/aos.css";
import "./Project.css";

const Project = () => {
  const projects = [
    {
      title: "AgroMaart - Online Fertilizer Shop",
      icon: "ri-shopping-cart-fill",
      description: "Built a user-friendly web application...",
      additionalInfo: "Technologies: Spring Boot, Hibernate, MySQL, React, HTML, CSS",
      imageSrc: require('../../assets/images/AgroMart.webp'),
      animation: "fade-left",
    },
    {
      title: "Students Management System",
      icon: "ri-book-2-fill",
      description: "The Students Management System is a web-based application...",
      additionalInfo: "Technologies: Spring Boot, Hibernate, Thymeleaf, MySQL",
      imageSrc: require('../../assets/images/studentManagementSystem.webp'),
      animation: "fade-left",
    },
    {
      title: "Employee Management System",
      icon: "ri-user-3-fill",
      description: "A Java-based system utilizing MySQL and Hibernate...",
      additionalInfo: "Technologies: Java, MySQL, Hibernate",
      animation: "fade-left",
    },
    {
      title: "Akshay Balte Portfolio",
      icon: "ri-portfolio-fill",
      description: "A portfolio website built with React, JavaScript, HTML, and CSS...",
      additionalInfo: "Technologies: React, JavaScript, HTML, CSS",
      animation: "fade-left",
    },
  ];

  return (
    <section id="project" className="project-section">
      <div className="project-container">
        <div className="section-header">
          <h2 className="section-title">Projects</h2>
        </div>
        <div className="projects-grid">
          {projects.map((project, index) => (
            index % 2 === 0 && (
              <div key={index} className="row">
                <TimelineCard {...projects[index]} />
                {projects[index + 1] && <TimelineCard {...projects[index + 1]} />}
              </div>
            )
          ))}
        </div>
      </div>
    </section>
  );
};

const TimelineCard = ({ title, icon, description, additionalInfo, imageSrc, animation }) => {
  return (
    <div className="timeline-card" data-aos={animation}>
      <div className="icon-container">
        {imageSrc && <img src={imageSrc} alt={title} className="icon-image" />}
      </div>
      <div className="card-content">
        <h3 className="card-title"><i className={`${icon} mr-2`}></i> {title}</h3>
        <p className="card-description">{description}</p>
        <p className="card-additional-info">{additionalInfo}</p>
      </div>
    </div>
  );
};

TimelineCard.propTypes = {
  title: PropTypes.string.isRequired,
  icon: PropTypes.string,
  description: PropTypes.string.isRequired,
  additionalInfo: PropTypes.string,
  imageSrc: PropTypes.string,
  animation: PropTypes.string.isRequired,
};

export default Project;
