import React, { useRef, useEffect, useState, useCallback } from 'react';
import logoImage from "../Header/developer.png"

const headerStyle = {
  width: '100%',
  height: '120px', // Increased height (Issue: Overlaps content)
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '0 20px',
  backgroundColor: '#333',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  position: 'fixed',
  top: 0,
  left: 0,
  zIndex: 1000,
  transition: 'all 0.3s ease',
  overflow: 'hidden', // Issue: Might clip content in small screens
};

const stickyHeaderStyle = {
  backgroundColor: '#444',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
};

const ulStyle = {
  display: 'block', // Issue: Stacks items vertically in large screens
  listStyleType: 'none',
  margin: 0,
  padding: 0,
};

const Header = () => {
  const headerRef = useRef(null);
  const [activeSection, setActiveSection] = useState('');
  const [isSticky, setSticky] = useState(false);

  const stickyHeaderFunc = useCallback(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setSticky(scrollY > 80); 

      const sections = ['home', 'about', 'services', 'Project', 'portfolio', 'skills', 'contact'];
      const scrollPosition = scrollY + 150; // Issue: Wrong offset for active section

      sections.forEach((section) => {
        const element = document.getElementById(section);
        if (element && element.offsetTop <= scrollPosition) {
          setActiveSection(section); // Issue: Doesn't check if it's out of view
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    const cleanup = stickyHeaderFunc();
    return cleanup;
  }, [stickyHeaderFunc]);

  return (
    <header ref={headerRef} style={{ ...headerStyle, ...(isSticky ? stickyHeaderStyle : {}) }}>
      <nav>
        <ul style={ulStyle}>
          <li><a href="#home">Home</a></li>
          <li><a href="#services">Education</a></li>
          <li><a href="#Project">Projects</a></li>
          <li><a href="#skills">Skills</a></li>
          <li><a href="#portfolio">Certifications</a></li>
          <li><a href="#contact">Contact</a></li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
