import React, { useRef, useEffect, useState } from 'react';

const Header = () => {
  const headerRef = useRef(null);
  const menuRef = useRef(null);
  const [isMenuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const stickyHeaderFunc = () => {
      if (
        document.body.scrollTop > 80 ||
        document.documentElement.scrollTop > 80
      ) {
        headerRef.current.classList.add('sticky_header');
      } else {
        headerRef.current.classList.remove('sticky_header');
      }
    };

    window.addEventListener('scroll', stickyHeaderFunc);
    return () => window.removeEventListener('scroll', stickyHeaderFunc);
  }, []);

  const handleClick = (e) => {
    e.preventDefault();
    const targetAttr = e.target.getAttribute('href');
    const targetElement = document.querySelector(targetAttr);

    if (targetElement) {
      const location = targetElement.offsetTop;

      window.scrollTo({
        top: location - 80,
        left: 0,
        behavior: 'smooth',
      });
    }
  };

  const toggleMenu = () => setMenuOpen(!isMenuOpen);

  return (
    <header
      ref={headerRef}
      className={`w-full h-[80px] leading-[80px] flex items-center justify-between px-4 shadow-lg transition-all duration-300 ${
        isMenuOpen ? 'menu-open' : ''
      }`}
      style={{
        backgroundColor: '#ffffff',
        zIndex: 1000,
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
      }}
    >
      {/* Logo */}
      <div className="container flex items-center justify-between">
        <div className="flex items-center gap-4">
          <span
            className="w-10 h-10 bg-blue-600 text-white text-2xl font-bold rounded-full flex items-center justify-center"
            style={{ boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)' }}
          >
            AB
          </span>
          <div className="leading-tight">
            <h2 className="text-xl font-bold text-gray-800 uppercase">
              Akshay
            </h2>
            <p className="text-gray-600 text-sm uppercase">Balte</p>
          </div>
        </div>
        {/* Menu */}
        <div
          className={`menu md:flex gap-6 items-center ${
            isMenuOpen ? 'block' : 'hidden'
          }`}
          ref={menuRef}
          style={{
            transition: 'transform 0.3s ease-in-out',
            transform: isMenuOpen ? 'translateX(0)' : 'translateX(100%)',
            backgroundColor: '#f8f9fa',
            position: 'absolute',
            top: '80px',
            right: 0,
            width: '100%',
            height: '100vh',
            display: isMenuOpen ? 'flex' : 'none',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 999,
          }}
        >
          <ul className="text-center text-lg">
            {['Home', 'About', 'Services', 'Portfolio', 'Contact', 'Skills'].map(
              (item, index) => (
                <li key={index} className="mb-6">
                  <a
                    href={`#${item.toLowerCase()}`}
                    onClick={handleClick}
                    className="text-gray-800 hover:text-blue-600 transition-colors duration-300"
                    style={{ fontSize: '18px', fontWeight: '500' }}
                  >
                    {item}
                  </a>
                </li>
              )
            )}
          </ul>
        </div>

        {/* Mobile Menu Toggle Icon */}
        <div className="md:hidden flex items-center" onClick={toggleMenu}>
          <i className="ri-menu-line text-3xl text-gray-800"></i>
        </div>
      </div>

      {/* Sticky Header Class */}
      <style jsx>{`
        .sticky_header {
          background-color: #ffffff;
          box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.1);
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          z-index: 999;
          transition: background-color 0.3s ease, box-shadow 0.3s ease;
        }
        .menu-open .menu {
          display: flex;
        }
        @media (min-width: 768px) {
          .menu {
            display: flex;
            background-color: transparent;
            height: auto;
            position: relative;
            top: unset;
            right: unset;
            width: auto;
            flex-direction: row;
          }
        }
      `}</style>
    </header>
  );
};

export default Header;
