import React, { useRef, useEffect, useState } from 'react';

// Hook to manage sticky state
const useSticky = (threshold = 80) => {
  const [isSticky, setSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => setSticky(window.scrollY > threshold);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [threshold]);

  return isSticky;
};

// Styles
const headerStyle = {
  width: '100%',
  height: '80px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '0 20px',
  backgroundColor: '#333',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  transition: 'all 0.3s ease',
  position: 'fixed',
  top: 0,
  left: 0,
  zIndex: 1000,
};

const stickyHeaderStyle = {
  backgroundColor: '#444',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
};

const logoStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '15px',
};

const logoTextStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  width: '40px',
  height: '40px',
  backgroundColor: '#1e40af',
  color: '#ffffff',
  textAlign: 'center',
  lineHeight: '40px',
  borderRadius: '50%',
  fontSize: '20px',
  fontWeight: '600',
  boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
};

const nameStyle = {
  lineHeight: '1.2',
};

const nameHeaderStyle = {
  fontSize: '1.5rem',
  color: '#ffffff',
  fontWeight: '700',
  textTransform: 'uppercase',
};

const nameSubHeaderStyle = {
  color: '#d3d3d3',
  fontSize: '1rem',
  fontWeight: '500',
  textTransform: 'uppercase',
};

const navStyle = {
  display: 'flex',
  alignItems: 'center',
};

const ulStyle = {
  display: 'flex',
  gap: '25px',
  listStyleType: 'none',
  margin: 0,
  padding: 0,
};

const linkStyle = {
  textDecoration: 'none',
  color: '#f1f1f1',
  fontSize: '1rem',
  fontWeight: '500',
  textTransform: 'uppercase',
  transition: 'color 0.3s ease, background-color 0.3s ease',
  padding: '8px 12px',
  borderRadius: '5px',
};

const activeLinkStyle = {
  color: '#ffffff', // White color for active link text
  backgroundColor: '#555555', // Dark gray background for contrast
  borderBottom: '2px solid #ffffff', // White underline to highlight the active link
};

// Mobile menu styles
const mobileNavStyle = {
  display: 'none',
};

const mobileMenuButton = {
  display: 'none',
  cursor: 'pointer',
  backgroundColor: 'transparent',
  border: 'none',
  color: '#f1f1f1',
  fontSize: '1.5rem',
};

// Mobile menu styles when active
const mobileNavActiveStyle = {
  display: 'block',
  position: 'absolute',
  top: '80px',
  right: '0',
  backgroundColor: '#333',
  padding: '20px',
  width: '200px',
};

const Header = () => {
  const headerRef = useRef(null);
  const [activeSection, setActiveSection] = useState('');
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);
  const isSticky = useSticky(80);

  const handleClick = (e) => {
    e.preventDefault();
    const targetAttr = e.target.getAttribute('href');
    const targetElement = document.querySelector(targetAttr);

    if (targetElement) {
      const headerHeight = headerRef.current.offsetHeight;
      window.scrollTo({
        top: targetElement.offsetTop - headerHeight,
        left: 0,
        behavior: 'smooth',
      });

      // Update the active section
      setActiveSection(targetAttr.replace('#', ''));
    }

    // Close the mobile menu after clicking
    if (isMobileMenuOpen) {
      setMobileMenuOpen(false);
    }
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header
      ref={headerRef}
      style={{
        ...headerStyle,
        ...(isSticky ? stickyHeaderStyle : {}),
      }}
    >
      <div style={logoStyle}>
        <span style={logoTextStyle}>AB</span>
        <div style={nameStyle}>
          <h2 style={nameHeaderStyle}>Akshay</h2>
          <p style={nameSubHeaderStyle}>Balte</p>
        </div>
      </div>

      <nav style={navStyle}>
        <ul style={ulStyle}>
          <li>
            <a
              href="#home"
              onClick={handleClick}
              style={activeSection === 'home' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Home Page"
            >
              Home Page
            </a>
          </li>
          <li>
            <a
              href="#services"
              onClick={handleClick}
              style={activeSection === 'services' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Educational Journey"
            >
              Educational Journey
            </a>
          </li>
          <li>
            <a
              href="#Project"
              onClick={handleClick}
              style={activeSection === 'Project' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Project Showcase"
            >
              Project Showcase
            </a>
          </li>
          <li>
            <a
              href="#skills"
              onClick={handleClick}
              style={activeSection === 'skills' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Skills & Expertise"
            >
              Skills & Expertise
            </a>
          </li>
          <li>
            <a
              href="#portfolio"
              onClick={handleClick}
              style={activeSection === 'portfolio' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Certification"
            >
              Certification
            </a>
          </li>
          <li>
            <a
              href="#contact"
              onClick={handleClick}
              style={activeSection === 'contact' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Let's Talk"
            >
              Let's Talk
            </a>
          </li>
        </ul>

        {/* Mobile Menu Button */}
        <button
          onClick={toggleMobileMenu}
          style={mobileMenuButton}
          aria-label="Toggle mobile navigation"
        >
          ☰
        </button>

        {/* Mobile Navigation */}
        <ul
          style={isMobileMenuOpen ? mobileNavActiveStyle : mobileNavStyle}
          aria-hidden={!isMobileMenuOpen}
        >
          <li>
            <a
              href="#home"
              onClick={handleClick}
              style={activeSection === 'home' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Home Page"
            >
              Home Page
            </a>
          </li>
          <li>
            <a
              href="#services"
              onClick={handleClick}
              style={activeSection === 'services' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Educational Journey"
            >
              Educational Journey
            </a>
          </li>
          <li>
            <a
              href="#Project"
              onClick={handleClick}
              style={activeSection === 'Project' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Project Showcase"
            >
              Project Showcase
            </a>
          </li>
          <li>
            <a
              href="#skills"
              onClick={handleClick}
              style={activeSection === 'skills' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Skills & Expertise"
            >
              Skills & Expertise
            </a>
          </li>
          <li>
            <a
              href="#portfolio"
              onClick={handleClick}
              style={activeSection === 'portfolio' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Certification"
            >
              Certification
            </a>
          </li>
          <li>
            <a
              href="#contact"
              onClick={handleClick}
              style={activeSection === 'contact' ? { ...linkStyle, ...activeLinkStyle } : linkStyle}
              aria-label="Let's Talk"
            >
              Let's Talk
            </a>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
