// EducationalJourney.js
import React from 'react';

const EducationalJourney = () => (
  <section style={styles.section}>
    <h2 style={styles.sectionTitle}>Educational Journey</h2>
    <div style={styles.timelineContainer}>
      <TimelineCard
        title="CDAC"
        icon="ri-graduation-cap-fill"
        description="PG-DAC, IACSD, Pune"
        date="March 2024 – August 2024"
        additionalInfo="60.8%"
        imageSrc={require('../../assets/images/cdac_logo.png')}
        animation="fade-left"
      />
      <TimelineCard
        title="Internship"
        icon="ri-robot-2-fill"
        description="Machine Learning Intern, Aiverient, Pune"
        date="June 2022 – February 2023"
        imageSrc={require('../../assets/images/Aivarient.png')}
        animation="fade-right"
      />
      <TimelineCard
        title="B.Tech Mechanical Engineering"
        icon="ri-graduation-cap-fill"
        description="DKTE Textile and Engineering Institute, Ichalkaranji"
        date="2016 – 2020"
        additionalInfo="7.78 CGPA"
        imageSrc={require('../../assets/images/btech.png')}
        animation="fade-left"
      />
      <TimelineCard
        title="HSC"
        icon="ri-school-fill"
        description="A.K. Junior College, Atpadi"
        date="2014 – 2016"
        additionalInfo="67.54%"
        imageSrc={require('../../assets/images/hsc.png')}
        animation="fade-right"
      />
      <TimelineCard
        title="SSC"
        icon="ri-school-fill"
        description="Shree Bhavnai Vidyalaya, Atpadi"
        date="2014"
        additionalInfo="90.60%"
        imageSrc={require('../../assets/images/ssc.png')}
        animation="fade-left"
      />
    </div>
  </section>
);

const styles = {
  section: {
    padding: '4rem 2rem',
    background: 'linear-gradient(135deg, #B3E5FC 30%, #e0f7fa 90%)',
    borderRadius: '0.5rem',
    fontFamily: 'Arial, sans-serif',
  },
  sectionTitle: {
    textAlign: 'center',
    color: '#ff5722',
    fontWeight: 'bold',
    fontSize: '2.5rem',
    marginBottom: '2rem',
    textTransform: 'uppercase',
    letterSpacing: '1px',
  },
  timelineContainer: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem',
    backgroundColor: '#e0f7fa',
    padding: '2rem',
    borderRadius: '0.5rem',
  },
  cardContainer: {
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    backgroundColor: '#ffecb3',
    padding: '1rem',
    borderRadius: '0.5rem',
  },
  cardTitle: {
    fontWeight: 'bold',
    fontSize: '1.4rem',
    color: '#bf360c',
  },
  cardDescription: {
    fontSize: '1rem',
    color: '#4b5563',
  },
  cardDate: {
    fontStyle: 'italic',
    color: '#8d6e63',
  },
  cardAdditionalInfo: {
    fontSize: '0.9rem',
    color: '#8d6e63',
  },
};

const TimelineCard = ({ title, icon, description, date, additionalInfo, imageSrc, animation }) => (
  <div style={styles.cardContainer}>
    <img src={imageSrc} alt={`Icon for ${title}`} style={{ width: '60px', height: '60px', objectFit: 'contain' }} />
    <div>
      <h3 style={styles.cardTitle}>
        <i className={`${icon} mr-2`}></i> {title}
      </h3>
      <p style={styles.cardDescription}>{description}</p>
      {date && <p style={styles.cardDate}>{date}</p>}
      {additionalInfo && <p style={styles.cardAdditionalInfo}>{additionalInfo}</p>}
    </div>
  </div>
);

export default EducationalJourney;
