// Modal.js
import React from 'react';
import data from "../../assets/dada/portfolioData";

const Modal = ({ setShowModal, activeID }) => {
    const activeItem = data.find(item => item.id === activeID);

    if (!activeItem) return null;

    return (
        <div className="modal">
            <div className="modal-content">
                <button onClick={() => setShowModal(false)} className="close-button">X</button>
                <div className="modal-details">
                    <h2>{activeItem.title}</h2> {/* Title Above Image */}
                    <img src={activeItem.imgUrl} alt={activeItem.title} className="modal-img" />
                    <p>{activeItem.description}</p> {/* Description Below Image */}
                </div>
            </div>
            <style jsx>{`
                .modal {
                    position: fixed;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                    background-color: rgba(0, 0, 0, 0.8);
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    z-index: 1000;
                }

                .modal-content {
                    position: relative;
                    background: #fff;
                    padding: 2rem;
                    border-radius: 10px;
                    width: 80%;
                    max-width: 600px;
                    text-align: center;
                }

                .close-button {
                    position: absolute;
                    top: 10px;
                    right: 10px;
                    background: none;
                    border: none;
                    font-size: 1.5rem;
                    cursor: pointer;
                    color: #333;
                }

                .modal-img {
                    max-width: 100%;
                    height: auto;
                    margin-top: 1rem;
                    margin-bottom: 1rem;
                    border-radius: 8px;
                }

                .modal-details h2 {
                    margin-bottom: 1rem;
                    font-size: 1.8rem;
                    color: #333;
                }

                .modal-details p {
                    font-size: 1rem;
                    color: #555;
                    margin-top: 1rem;
                }
            `}</style>
        </div>
    );
};

export default Modal;
