import React from 'react';

const Skills = () => {
  return (
    <section id="Skills" className="skills-section">
      <div className="container">
        <h2 className="section-title">My Skills</h2>

        <div className="skills-container">
          <div className="skills-category">
            <h3 className="category-title">Frontend</h3>
            <div className="badge-group">
              <Badge src="https://img.shields.io/badge/-JavaScript-F7DF1E?style=flat&logo=javascript&logoColor=black" alt="JavaScript" />
              <Badge src="https://img.shields.io/badge/-HTML5-E34F26?style=flat&logo=html5&logoColor=white" alt="HTML5" />
              <Badge src="https://img.shields.io/badge/-CSS3-1572B6?style=flat&logo=css3&logoColor=white" alt="CSS3" />
              <Badge src="https://img.shields.io/badge/-React-61DAFB?style=flat&logo=react&logoColor=black" alt="React" />
            </div>
          </div>

          {/* Add other sections similarly */}
          <div className="skills-category">
            <h3 className="category-title">Backend</h3>
            <div className="badge-group">
              <Badge src="https://img.shields.io/badge/-Java-007396?style=flat&logo=java&logoColor=white" alt="Java" />
              <Badge src="https://img.shields.io/badge/-Node.js-339933?style=flat&logo=node.js&logoColor=white" alt="Node.js" />
              <Badge src="https://img.shields.io/badge/-CSharp-239120?style=flat&logo=c-sharp&logoColor=white" alt="C#" />
              <Badge src="https://img.shields.io/badge/-C++-00599C?style=flat&logo=c%2B%2B&logoColor=white" alt="C++" />
            </div>
          </div>
          {/* Continue adding other sections here */}
        </div>
      </div>

      <style jsx>{`
        .skills-section {
          padding: 4rem;
          background-color: #f5f5f5;
        }

        .container {
          max-width: 1200px;
          margin: 0 auto;
        }

        .section-title {
          text-align: center;
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 3rem;
          color: #333;
        }

        .skills-container {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
          gap: 2rem;
        }

        .skills-category {
          background-color: #ffffff;
          border-radius: 12px;
          box-shadow: 0px 6px 15px rgba(0, 0, 0, 0.1);
          padding: 2rem;
          text-align: center;
          max-width: 300px;
          margin: 1rem auto;
        }

        .category-title {
          font-size: 1.8rem;
          font-weight: 600;
          margin-bottom: 1.5rem;
          color: #007396;
        }

        .badge-group {
          display: flex;
          flex-wrap: wrap;
          justify-content: center;
          gap: 0.5rem;
        }

        .badge-group img {
          border-radius: 8px;
          transition: transform 0.3s;
        }

        .badge-group img:hover {
          transform: scale(1.1);
        }
      `}</style>
    </section>
  );
};

const Badge = ({ src, alt }) => (
  <span className="badge">
    <img src={src} alt={alt} />
  </span>
);

export default Skills;
