import React, { useRef, useEffect, useState, useCallback } from 'react';
import logoImage from "../Header/developer.png";

// Style constants
const COLORS = {
  primary: '#1e40af',
  text: '#f1f1f1',
  background: '#333',
  stickyBackground: '#444',
  subText: '#d3d3d3'
};

const HEADER_HEIGHT = 100;

const headerStyle = {
  width: '100%',
  height: `${HEADER_HEIGHT}px`,
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
  padding: '0 20px',
  backgroundColor: COLORS.background,
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  position: 'fixed',
  top: 0,
  left: 0,
  zIndex: 1000,
  transition: 'all 0.3s ease',
};

const logoStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '15px',
};

const logoImageStyle = {
  width: '60px',
  height: '60px',
  borderRadius: '50%',
  objectFit: 'cover',
};

const nameStyle = {
  lineHeight: '1.2',
};

const nameHeaderStyle = {
  fontSize: '2rem',
  color: '#ffffff',
  fontWeight: '700',
  textTransform: 'uppercase',
};

const nameSubHeaderStyle = {
  color: COLORS.subText,
  fontSize: '1rem',
  fontWeight: '500',
};

const navStyle = {
  display: 'flex',
  alignItems: 'center',
};

const ulStyle = {
  display: 'flex',
  gap: '25px',
  listStyleType: 'none',
  margin: 0,
  padding: 0,
};

const baseLinkStyle = {
  textDecoration: 'none',
  fontSize: '1rem',
  fontWeight: '500',
  textTransform: 'uppercase',
  transition: 'all 0.3s ease',
  padding: '8px 12px',
  borderRadius: '5px',
};

const linkStyle = {
  ...baseLinkStyle,
  color: COLORS.text,
  backgroundColor: 'transparent',
};

const activeLinkStyle = {
  ...baseLinkStyle,
  color: COLORS.primary,
  backgroundColor: COLORS.text,
  borderBottom: `2px solid ${COLORS.primary}`,
};

const Header = () => {
  const headerRef = useRef(null);
  const [activeSection, setActiveSection] = useState('');
  const [isSticky, setSticky] = useState(false);

  // Intersection Observer for active section detection
  const setupObserver = useCallback(() => {
    const sections = ['home', 'education', 'projects', 'skills', 'certifications', 'contact'];
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      {
        root: null,
        rootMargin: `-${HEADER_HEIGHT}px 0px 0px 0px`,
        threshold: 0.5
      }
    );

    sections.forEach(sectionId => {
      const section = document.getElementById(sectionId);
      if (section) observer.observe(section);
    });

    return () => {
      sections.forEach(sectionId => {
        const section = document.getElementById(sectionId);
        if (section) observer.unobserve(section);
      });
    };
  }, []);

  // Scroll listener for sticky header
  const handleScroll = useCallback(() => {
    const scrollY = window.scrollY;
    setSticky(scrollY > HEADER_HEIGHT / 2);
  }, []);

  useEffect(() => {
    const scrollCleanup = () => {
      window.removeEventListener('scroll', handleScroll);
    };
    window.addEventListener('scroll', handleScroll);
    const observerCleanup = setupObserver();
    
    return () => {
      scrollCleanup();
      observerCleanup();
    };
  }, [handleScroll, setupObserver]);

  const handleNavigation = (e) => {
    e.preventDefault();
    const anchor = e.target.closest('a');
    if (!anchor) return;

    const targetId = anchor.getAttribute('href');
    const targetElement = document.querySelector(targetId);

    if (targetElement) {
      const headerHeight = headerRef.current?.offsetHeight || 0;
      const targetPosition = targetElement.offsetTop - headerHeight;
      window.scrollTo({
        top: targetPosition,
        left: 0,
        behavior: 'smooth'
      });
    }
  };

  return (
    <header
      ref={headerRef}
      style={{
        ...headerStyle,
        backgroundColor: isSticky ? COLORS.stickyBackground : COLORS.background,
        boxShadow: isSticky ? '0 4px 8px rgba(0, 0, 0, 0.3)' : headerStyle.boxShadow
      }}
    >
      <div style={logoStyle}>
        <img src={logoImage} alt="Developer Logo" style={logoImageStyle} />
        <div style={nameStyle}>
          <h2 style={nameHeaderStyle}>Akshay Balte</h2>
          <span style={nameSubHeaderStyle}>An Inspiring Developer</span>
        </div>
      </div>

      <nav style={navStyle} aria-label="Main navigation">
        <ul style={ulStyle}>
          {['home', 'education', 'projects', 'skills', 'certifications', 'contact'].map((section) => (
            <li key={section}>
              <a
                href={`#${section}`}
                onClick={handleNavigation}
                style={activeSection === section ? activeLinkStyle : linkStyle}
                aria-label={`Navigate to ${section} section`}
              >
                {section.charAt(0).toUpperCase() + section.slice(1)}
              </a>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
