import React from 'react';

// Portfolio Data
const portfolios = [
  {
    id: "01",
    imgUrl: "https://via.placeholder.com/300x200", // Use actual image URLs or relative paths
    category: "Internship",
    title: "Aivarient",
    description: "Completed internship in machine learning using Python at Aivarient.",
    technologies: ["Machine Learning", "Python"],
    siteUrl: "#",
  },
  {
    id: "02",
    imgUrl: "https://via.placeholder.com/300x200",
    category: "Pre-CAT",
    title: "Sunbeam Institute",
    description: "Completed a pre-CAT course for CDAC at Sunbeam Institute.",
    technologies: ["C", "C++", "Data Structures"],
    siteUrl: "#",
  },
  {
    id: "03",
    imgUrl: "https://via.placeholder.com/300x200",
    category: "Certification",
    title: "Machine Learning with Python by IBM",
    description: "Successfully completed IBM certification in Machine Learning with Python.",
    technologies: ["Python", "Machine Learning"],
    siteUrl: "#",
  },
  {
    id: "04",
    imgUrl: "https://via.placeholder.com/300x200",
    category: "UX",
    title: "SQL",
    description: "Successfully completed the MySQL course taught by Great Learning.",
    technologies: ["SQL"],
    siteUrl: "#",
  },
];

// Portfolio Component
const Portfolio = () => {
  return (
    <section style={styles.portfolioSection}>
      <h2 style={styles.portfolioTitle}>My Portfolio</h2>
      <div style={styles.portfolioGrid}>
        {portfolios.map((portfolio) => (
          <div style={styles.portfolioItem} key={portfolio.id}>
            <img
              src={portfolio.imgUrl}
              alt={portfolio.title}
              style={styles.portfolioImg}
            />
            <div style={styles.portfolioContent}>
              <h3 style={styles.portfolioCategory}>{portfolio.category}</h3>
              <h4 style={styles.portfolioTitle}>{portfolio.title}</h4>
              <p style={styles.portfolioDescription}>{portfolio.description}</p>
              <div style={styles.portfolioTechnologies}>
                {portfolio.technologies.map((tech, index) => (
                  <span key={index} style={styles.portfolioTech}>
                    {tech}
                  </span>
                ))}
              </div>
              <a href={portfolio.siteUrl} style={styles.portfolioLink}>
                View More
              </a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

// Inline Styles
const styles = {
  portfolioSection: {
    padding: '60px 20px',
    maxWidth: '1200px',
    margin: '0 auto',
  },
  portfolioTitle: {
    textAlign: 'center',
    fontSize: '2.5rem',
    marginBottom: '50px',
    color: '#2b2d42',
  },
  portfolioGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
  },
  portfolioItem: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out',
  },
  portfolioItemHover: {
    transform: 'translateY(-10px)',
  },
  portfolioImg: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  portfolioContent: {
    padding: '20px',
    textAlign: 'center',
  },
  portfolioCategory: {
    fontSize: '1rem',
    fontWeight: 'bold',
    color: '#ff6b6b',
    marginBottom: '10px',
  },
  portfolioDescription: {
    fontSize: '1rem',
    color: '#666',
    marginBottom: '15px',
  },
  portfolioTechnologies: {
    marginBottom: '20px',
  },
  portfolioTech: {
    display: 'inline-block',
    backgroundColor: '#ef233c',
    color: '#fff',
    fontSize: '0.9rem',
    padding: '5px 10px',
    borderRadius: '5px',
    marginRight: '5px',
    marginBottom: '5px',
  },
  portfolioLink: {
    display: 'inline-block',
    padding: '10px 20px',
    backgroundColor: '#2b2d42',
    color: '#fff',
    textDecoration: 'none',
    borderRadius: '5px',
    fontSize: '1rem',
    transition: 'background-color 0.3s',
  },
  portfolioLinkHover: {
    backgroundColor: '#ff6b6b',
  },
};

export default Portfolio;
